import React, { useState, useEffect } from 'react';
import getWeb3 from '../utils/getWeb3';
import CarbonCredit from '../contracts/CarbonCredit.sol'

function CarbonCreditDashboard() {
    const [transactions, setTransactions] = useState([]);
    const [companyName, setCompanyName] = useState('');
    const [creditAmount, setCreditAmount] = useState(0);
    const [web3, setWeb3] = useState(null);
    const [carbonCreditContract, setCarbonCreditContract] = useState(null);

    useEffect(() => {
        const initializeWeb3 = async () => {
            try {
                const web3Instance = await getWeb3();
                const networkId = await web3Instance.eth.net.getId();
                const deployedNetwork = CarbonCredit.networks[networkId];
                const contractInstance = new web3Instance.eth.Contract(
                    CarbonCredit.abi,
                    deployedNetwork && deployedNetwork.address
                );
                setWeb3(web3Instance);
                setCarbonCreditContract(contractInstance);
                console.log('Web3 and contract instances are available:', web3Instance, contractInstance);

            } catch (error) {
                console.error('Error connecting to Web3', error);
            }
        };

        initializeWeb3();
    }, []);

    const addTransaction = async () => {
        // In a real application, you would send a transaction to the smart contract
        // to record the carbon credit transaction and retrieve this data from the blockchain.
        if (!web3 || !carbonCreditContract) {
            return;
        }

        try {
            // In a real application, you would send a transaction to the smart contract
            // to record the carbon credit transaction and retrieve this data from the blockchain.
            await carbonCreditContract.methods.recordTransaction(companyName, creditAmount).send({
                from: web3.eth.defaultAccount, // Use the selected Ethereum account
            });

            const newTransaction = {
                company: companyName,
                credits: creditAmount,
                timestamp: new Date().toLocaleString(),
            };

            setTransactions([...transactions, newTransaction]);

            // Clear the input fields
            setCompanyName('');
            setCreditAmount(0);
        } catch (error) {
            console.error('Error adding transaction', error);
        }
    };

    return (
        <div className="carbon-trading-app">
            <h1>Carbon Credit Trading Dashboard</h1>
            <div className="transaction-form">
                <input
                    type="text"
                    placeholder="Company Name"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="Credit Amount"
                    value={creditAmount}
                    onChange={(e) => setCreditAmount(parseInt(e.target.value, 10))}
                />
                <button onClick={addTransaction}>Add Transaction</button>
            </div>
            <div className="transaction-list">
                <h2>Recent Transactions</h2>
                <ul>
                    {transactions.map((transaction, index) => (
                        <li key={index}>
                            <strong>Company:</strong> {transaction.company} |
                            <strong>Credits:</strong> {transaction.credits} |
                            <strong>Timestamp:</strong> {transaction.timestamp}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default CarbonCreditDashboard;
