// Web3Context.js
import React, { createContext, useContext, useEffect, useState } from 'react';
import Web3 from 'web3';
import CarbonCreditContract from '../contracts/CarbonCredit.sol';

const Web3Context = createContext();

export function Web3Provider({ children }) {
    const [web3, setWeb3] = useState(null);
    const [carbonCreditContract, setCarbonCreditContract] = useState(null);

    useEffect(() => {
        async function initializeWeb3() {
            if (window.ethereum) {
                const initWeb3 = new Web3(window.ethereum);
                setWeb3(initWeb3);
                console.log(web3);
                getCarbonCreditContract(web3);
            } else if (window.web3) {
                const initWeb3 = new Web3(window.web3.currentProvider);
                setWeb3(initWeb3);
                console.log(web3);

                getCarbonCreditContract(web3);
            } else {
                // Non-dapp browsers or no web3 provider
                throw new Error('No web3 provider detected. Please install MetaMask or use a dapp browser.');
            }

        }
        initializeWeb3();
    }, []);

    const getCarbonCreditContract = async (web3) => {

        try {
            const networkId = await web3.eth.net.getId();
            console.log(networkId)
            const deployedNetwork = CarbonCreditContract.networks[networkId];
            console.log(deployedNetwork)
            if (deployedNetwork) {
                const contractInstance = new web3.eth.Contract(
                    CarbonCreditContract.abi,
                    deployedNetwork.address
                );
                setCarbonCreditContract(contractInstance);
            } else {
                console.error('The contract is not deployed on the current network');
            }
        } catch (error) {
            console.error('Error connecting to Web3', error);
        }
    }

    return (
        <Web3Context.Provider value={{ web3, carbonCreditContract }}>
            {children}
        </Web3Context.Provider>
    );
}

export function useWeb3() {
    return useContext(Web3Context);
}
